resourceQuery:
SELECT ?etype ?elabel ?elink ?eabstract ?ethumbnail WHERE {
    OPTIONAL{ %RES% rdf:type ?etype .}
    OPTIONAL{ %RES% rdfs:label ?elabel .            FILTER (lang(?elabel) = 'en') }
    OPTIONAL{ %RES% <http://xmlns.com/foaf/0.1/isPrimaryTopicOf> ?elink . }
    OPTIONAL{ %RES% <http://dbpedia.org/ontology/abstract> ?eabstract . 
            FILTER (lang(?eabstract) = 'en')
    }
    OPTIONAL{ %RES% <http://dbpedia.org/ontology/thumbnail> ?ethumbnail . }

} LIMIT 1
